            self.cell_field[10*i:10*(i+1), 10*i:10*(i+1), 0] = cell

        for i in range(5):
            cell = self.new_cell(self.cancer_cell)
            self.cell_field[50+10*i:60+10*i, 50+10*i:60+10*i, 0] = cell

